import React, { Component } from "react";
import "./ChallengeStats.js";

class ChallengeStats extends Component {
    constructor(){
        super();
        this.state = {
            featureMainWins: ""
        }
    }

    componentDidMount(){
        const challenges = this.props.challenges;
        const mainWins = challenges.filter((value) => {
            return value.type === "main" && value.won === true

        });
        this.setState({
            featureMainWins: mainWins.length
        })
    }

    // use filter to make two arrays of main challange and mini challenge to then filter over?

    render(){
        return (
            <div></div>
        )
    }
}

export default ChallengeStats;
